mapper.select:
select 
from comment
where status=0
and entity_type= #{entityType}
and entity_id=#{entityId}
order by create_time asc
limit #{offset}, #{limit}

select count(id) from comment
where status=0
and entity_type= #{entityType}
and entity_id=#{entityId}

select 
from comment
where status=0 and user_id = #{userId}
order by create_time asc
limit #{offset}, #{limit}

select count(id)
from comment
where user_id = #{userId}
